import React from "react";
import {
    CCarousel,
    CCarouselItem,
    CImage,
    CCarouselCaption,
} from "@coreui/react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Carrousel() {
    function changeText(e) {
        const textSlide = document.querySelector(".text-group");
        textSlide.style.transform = `translateY(${-e * 2.2}rem)`;
    }

    return (
        <div className="images">
            <CCarousel indicators onSlide={(e) => changeText(e)}>
                <CCarouselItem>
                    <CImage
                        className="d-block w-100"
                        src={require("../assets/images/logo.png")}
                        alt="slide 1"
                    />
                    <CCarouselCaption>
                        <h2>Saia do vermelho com a Green!</h2>
                    </CCarouselCaption>
                </CCarouselItem>
                <CCarouselItem>
                    <CImage
                        className="d-block w-100"
                        src={require("../assets/images/leaf.png")}
                        alt="slide 2"
                    />
                    <CCarouselCaption>
                        <h2>Um banco sustent√°vel.</h2>
                    </CCarouselCaption>
                </CCarouselItem>
                <CCarouselItem>
                    <CImage
                        className="d-block w-100"
                        src={require("../assets/images/img1.png")}
                        alt="slide 3"
                    />
                    <CCarouselCaption>
                        {" "}
                        <h2>Torne seu bolso mais leve.</h2>
                    </CCarouselCaption>
                </CCarouselItem>
            </CCarousel>
        </div>
    );
}
